import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Header/slideshow.css";
import Promotions from "./Header/promotions.json";

const Slideshow = () => {
  const settings = {
    dots: true,
    Infinity: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScrool: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };
  return (
    <Slider className="slider-container" {...settings}>
      {Promotions.promotions.map((promotion) => (
        <div key={title} className="card-promotions">
          <h2 className="h2-promotions">{title}</h2>
          <img
            className="card-image-promotions"
            src={promotion.image}
            alt={title}
          />
        </div>
      ))}
    </Slider>
    /*
    <Slider className="slider-container" {...settings}>
      {Promotions.promotions.map((promotion) => (
        <div className="image-container" key={promotion.title}>
          <h2 className="h2-promotions">{title}</h2>
          <img src={promotion.image} alt={promotion.title} width={450} />
        </div>
      ))}
    </Slider>*/
  );
};

export default Slideshow;
