  const { gameName } = useParams();

  // Ovdje preuzmite sliku na osnovu imena igre (gameName) iz vašeg JSON-a ili odakle god da je
  const gameImage = Games.casinogame.find((game) => game.name === gameName)?.image;

  return (
    <div>
      {/* Prikazivanje samo slike */}
      <img src={gameImage} alt={gameName} />
    </div>
  );
}

export default Gameplay;
