import { useEffect, useState } from "react";
import Games from "./casinogame.json";
import "../AllGames/allGames.css";

/*export default function AllGames() {
    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/todos')
            .then(response => response.json())
            .then(json => console.log(json))
    }, []);

*/
function GameDetails({ game }) {
  return (
    <div className="game-details">
      <h2>{game.name}</h2>
      <img src={game.image} alt={game.name} />
      <p>{game.description}</p>
      {/* Ovdje možete dodati detalje igre, kao što su gameplay video ili druge informacije */}
    </div>
  );
}
function AllGames() {
  const [expandedGame, setExpandedGame] = useState(null);
  const [isImageExpanded, setImageExpanded] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null); 

  const toggleDescription = (gameName) => {
    setExpandedGame(gameName === expandedGame ? null : gameName);
  };
  const toggleImage = () => {
    setImageExpanded(!isImageExpanded);
  };
  const handleGameClick = (gameName) => {
    setSelectedGame(Games.casinogame.find((game) => game.name === gameName));
  };

  return (
    <div>
      <h1 className="h1-games">All Games</h1>
      <div className="card-container-games">
        {Games.casinogame.map((casinogame) => {
          const { name, image, description } = casinogame;
          const isExpanded = name === expandedGame;
          const isLongDescription = description.split(" ").length > 3;
          return (
            <div key={name} className="card-games">
              <h2 className="h2-games">{name}</h2>
              <img className={`card-img ${isImageExpanded ? "expanded" : ""}`} 
              src={casinogame.image} 
              alt={name}
              onClick={() => {
                toggleImage();
                handleGameClick(name);
              }} />
              <div className={`card-description-games ${isExpanded && "expanded"}`}>
                {isLongDescription && !isExpanded
                  ? `${description.split(" ").slice(0, 3).join(" ")}...`
                  : description}
              </div>
              {isLongDescription && (
                <button
                  className="btn-link-games"
                  onClick={() => toggleDescription(name)}
                >
                  {isExpanded ? "Prikaži manje" : "Prikaži više"}
                </button>
              )}
            </div>
          );
        })}
      </div>
      {selectedGame && <GameDetails game={selectedGame} />}
    </div>
  );
}
export default AllGames;
