import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./Header/slideshow.css";
import Promotions from "./Header/promotions.json";

const Slideshow = () => {
  const settings = {
    dots: true,
    Infinity: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScrool: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };
  return (
    <Slider className="slider-container" {...settings}>
      {Promotions.Promotions.map((Promotions) => (
        <div className="image-container" key={Promotions.title}>
          <img src={Promotions.image} alt={Promotions.title} width={450} />
        </div>
      ))}
    </Slider>
  );
};

export default Slideshow;
