import { useEffect, useState } from "react";
import axios from 'axios';

const AllGames = () => {
	const [games, setGames] = useState([]);
  
	useEffect(() => {
	  // Definirajte funkciju za dohvaćanje podataka
	  const fetchGames = async () => {
		try {
		  const response = await axios.get('http://localhost:5000/componenta/AllGames/allgames');
		  setGames(response.data);
		} catch (error) {
		  console.error('Greška pri dohvaćanju podataka:', error);
		}
	  };
	  
  
	  // Pozovite funkciju za dohvaćanje podataka
	  fetchGames();
	}, []);
  
return (
	<div
	style={{
		display: 'flex',
		justifyContent: 'left',
		alignItems: 'start',
		height: '100vh',
		fontStyle :'italic',
		fontFamily : 'dancing-script'
	}}
	>
	<h1>All games</h1>

    <div>
    Different ways to fetch Data
</div>
	</div>
)
};
export default AllGames;
