import React, { useState } from "react";
import { Link } from "react-router-dom";
import Games from "./casinogame.json";
import "../AllGames/allGames.css";

function AllGames() {
  const [expandedGame, setExpandedGame] = useState(null);

  const toggleDescription = (gameName) => {
    setExpandedGame(gameName === expandedGame ? null : gameName);
  };

  return (
    <app-game-category >
			<div className="grid-main">
				<div className="home-slot">
					<div className="page-slot">
						<div className="grid-header" align-items-center pt-2>
							<h2 class="sm-head headline text-center with-button">
								<div className="headline-border">
									ALLE SPIELE
								</div>
							</h2>
			<div className="div-games">
			<div className="default">
				<div className="grid-games">
					<div className="gallery-img">
					<div className="card-container">
			{Games.games.map((game) => {
	const { name, image } = game;  
	const isExpanded = name === expandedGame;
			return (
				<div key={name} >
				<img className="card-image" src={game.image} alt={name} />
				</div>
			);
			})}
			</div>
			</div>
			</div>
			</div>
	</div> 
						</div>
					</div>
				</div>
				</div>
		</app-game-category>
  );
}

export default AllGames;
