import { useEffect } from "react";

export default function AllGames() {
    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/todos')
            .then(response => response.json())
            .then(json => console.log(json))
    }, []);
return (
	<div
	style={{
		display: 'flex',
		justifyContent: 'left',
		alignItems: 'start',
		height: '100vh',
		fontStyle :'italic',
		fontFamily : 'dancing-script'
	}}
	>

		  <h1 className="h1">All Games</h1>
		  <div className="card-container">
			{Games.games.map((game) => {
			  const { name, image, description } = game;
			  const isExpanded = name === expandedGame;
			  const isLongDescription = description.split(" ").length > 3;
			  return (
				<div key={name} className="card">
				  <h2 className="h2">{name}</h2>
				  <img className="card-image" src={game.image} alt={name} />
				  <div className={`card-description ${isExpanded && "expanded"}`}>
					{isLongDescription && !isExpanded
					  ? `${description.split(" ").slice(0, 3).join(" ")}...`
					  : description}
				  </div>
				  {isLongDescription && (
					<button
					  className="btn-link"
					  onClick={() => toggleDescription(name)}
					>
					  {isExpanded ? "Prikaži manje" : "Prikaži više"}
					</button>
				  )}
				</div>
			  );
			})}
		  </div>
	</div>

   
)
};
