import React, { useState } from "react";
import "../App.css";
import Navbar from "../components/Header";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./home";
import About from "./about";
import Register from "./register";
import Products from "./products";
import Login from "./login";
import Games from "../games.json";
import Slideshow from "../components/Slideshow/slideshow";

function Index() {
	const [expandedGame, setExpandedGame] = useState(null);
  
	const toggleDescription = (gameName) => {
	  setExpandedGame(gameName === expandedGame ? null : gameName);
	};
	const [expandedPromotion, setExpandedPromotion] = useState(null);
  
	const toggleDescription1 = (promotionTitle) => {
	  setExpandedPromotion(
		promotionTitle === expandedPromotion ? null : promotionTitle
	  );
	};
  
	return (
	  <div className="app">
		<>
		  <div>
			<Slideshow />
		  </div>
		  <div className="home-container">
		  <h1>Popular Games</h1>
		  <div className="card-container">
			{Games.games.map((game) => {
			  const { name, image, description } = game;
			  const isExpanded = name === expandedGame;
			  const isLongDescription = description.split(" ").length > 3;
			  return (
				<div key={name} className="card">
				  <h2 className="h2">{name}</h2>
				  <img className="card-image" src={game.image} alt={name} />
				  <div className={`card-description ${isExpanded && "expanded"}`}>
					{isLongDescription && !isExpanded
					  ? `${description.split(" ").slice(0, 3).join(" ")}...`
					  : description}
				  </div>
				  {isLongDescription && (
					<button
					  className="btn-link"
					  onClick={() => toggleDescription(name)}
					>
					  {isExpanded ? "Prikaži manje" : "Prikaži više"}
					</button>
				  )}
				</div>
			  );
			})}
		  </div>
		  </div>
		</>
	  
	  </div>
	);
  }
  
  export default Index;
