import React, { useState } from 'react';
import './App.css';
import Navbar from './components/Header';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages';
import About from './pages/about';
import Register from './pages/register';
import Products from './pages/products';
import Login from './pages/login';
import Games from './games.json';
import Promotions from './promotions.json';
import { Slider } from "./components/Slider/slider";

import { slider } from "./dataSlider.json";

function App() {
  const [expandedGame, setExpandedGame] = useState(null);

  const toggleDescription = (gameName) => {
    setExpandedGame(gameName === expandedGame ? null : gameName);
  }
    const [expandedPromotion, setExpandedPromotion] = useState(null);

  const toggleDescription1 = (promotionTitle) => {
    setExpandedPromotion(promotionTitle === expandedPromotion ? null : promotionTitle);
  };


  return (


    <div className='app'>
    <>
      <Router>
        <Navbar />
        <Routes>
          <Route path='/' exact element={<Home />} />
          <Route path='/about' element={<About />} />
          <Route path='/register' element={<Register />} />
          <Route path='/products' element={<Products />} />
          <Route path='/login' element={<Login />} />
        </Routes>
      </Router>
      <Slider data={slides} />
      <h1>Popular Games</h1>
      <div className='card-container'>
        {Games.games.map((game) => {
          const { name, image, description } = game;
          const isExpanded = name === expandedGame;
          const isLongDescription = description.split(' ').length > 3;
          return (
            <div key={name} className='card'>
              <h2 className="h2">{name}</h2>
              <img className="card-image"src={game.image} alt={name} />
              <div className={`card-description ${isExpanded && 'expanded'}`}>
                {isLongDescription && !isExpanded
                  ? `${description.split(' ').slice(0, 3).join(' ')}...`
                  : description}
              </div>
              {isLongDescription && (
                <button className="btn-link" onClick={() => toggleDescription(name)}>
                  {isExpanded ? 'Prikaži manje' : 'Prikaži više'}
                </button>
              )}
            </div>
          );
        })}
      </div>
      <h1>Promotions</h1>
      <div className='card-container-promotions'>
        {Promotions.promotions.map((promotion) => {
          const { title, images } = promotion;
          return (
            <div key={title} className='card-promotions'>
              <h2 className="h2-promotions">{title}</h2>
              <img className="card-image-promotions"src={promotion.image} alt={title} />

            </div>
          );
        })}
      </div>
    </>
    </div>
  );
}

export default App;
