import React, { useState } from "react";
import { Link } from "react-router-dom";
import Games from "./casinogame.json";
import "../AllGames/allGames.css";

function AllGames() {
  const [expandedGame, setExpandedGame] = useState(null);

  const toggleDescription = (gameName) => {
    setExpandedGame(gameName === expandedGame ? null : gameName);
  };

  return (
    <div className="app">
      <>
      <app-game-category>
      <div className="grid-main">
        <div className="home-slot">
          <div className="page-slot">
            <div className="grid-header" align="center">
              <h2 className="sm-head headline text-center with-button">
                <div className="headline-border">
                  ALLE SPIELE
                </div>
              </h2>
            </div>
            <div className="div-games">
              <div className="default">
                <div className="grid-games">
                  <div className="gallery-img">
                    <div className="card-container">
                    {Games.casinogame.map((casinogame) => {
          const { name, image } = casinogame;
          return (
            <div key={name} className="card-games">
              <h2 className="h2-games">{name}</h2>
              {}
              <Link to={`/gameplay/${name}`}>
                <img
                  className="card-img"
                  src={image}
                  alt={name}
                />
              </Link>
            </div>
          );
        })}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </app-game-category>
      </>
    </div>
   
  );
}

export default AllGames;
