import React, { useState } from 'react';
import './App.css';
import Navbar from './components/Header';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages';
import About from './pages/about';
import Events from './pages/events';
import Products from './pages/products';
import Signup from './pages/signup';
import Games from './games.json';

function App() {
  const [showFullDescription, setShowFullDescription] = useState(false);

  const toggleDescription = () => {
    setShowFullDescription(!showFullDescription);
  };

  const getDescriptionPreview = (description, maxWords) => {
    const words = description.split(' ');
    if (words.length <= maxWords) {
      return description;
    }
    const preview = words.slice(0, maxWords).join(' ');
    return `${preview}...`;
  };

  return (
    <>
      <Router>
        <Navbar />
        <Routes>
          <Route path='/' exact component={Home} />
          <Route path='/about' component={About} />
          <Route path='/events' component={Events} />
          <Route path='/products' component={Products} />
          <Route path='/sign-up' component={Signup} />
        </Routes>
      </Router>
      <h1>Popular Games</h1>
      <div className='card-container'>
        {Games.games.map((game) => (
          <div key={game.name} className='card'>
            <h2>{game.name}</h2>
            <img src={require(`./components/img/${game.path}`).default} alt={game.name} />
            <p className='card-description'>
              {showFullDescription ? game.description : getDescriptionPreview(game.description, 3)}
            </p>
            {!showFullDescription && (
              <button onClick={toggleDescription}>Prikaži više</button>
            )}
          </div>
        ))}
      </div>
    </>
  );
}

export default App;
